CREATE TABLE `dkp_news` (`news_id`  smallint(5) UNSIGNED NOT NULL AUTO_INCREMENT ,`news_headline`  varchar(255) CHARACTER SET utf8 COLLATE utf8_bin NOT NULL DEFAULT '' ,`news_message`  text CHARACTER SET utf8 COLLATE utf8_bin NOT NULL ,`news_date`  int(11) NOT NULL ,`nocomments`  smallint(4) NOT NULL ,`news_permissions`  smallint(4) UNSIGNED NOT NULL ,`news_flags`  smallint(4) UNSIGNED NOT NULL DEFAULT 0 ,`user_id`  smallint(5) UNSIGNED NOT NULL ,`user_name`  varchar(30) CHARACTER SET utf8 COLLATE utf8_bin NOT NULL ,PRIMARY KEY (`news_id`),UNIQUE INDEX `news_id` USING BTREE (`news_id`) ,INDEX `user_id` USING BTREE (`user_id`) )ENGINE=InnoDBDEFAULT CHARACTER SET=utf8 COLLATE=utf8_binAUTO_INCREMENT=1ROW_FORMAT=COMPACT;